$(function(){describe('RSS Feeds',function(){it('are defined',function(){expect(allFeeds).toBeDefined();expect(allFeeds.length).not.toBe(0);});beforeEach(function(){it('are defined',function(){expect(allFeeds).toBeDefined();expect(allFeeds.length).not.toBe(0);});});it('urls are defined',function(){allFeeds.forEach(function(element){expect(element.url).toBeDefined();expect(element.url.length).not.toBe(0);});});it('names are defined',function(){allFeeds.forEach(function(element){expect(element.name).toBeDefined();expect(element.name.length).not.toBe(0);});});});describe('The menu',function(){it('is hidden by default',function(){expect($('body').hasClass('menu-hidden')).toBeTruthy();});it('changes visibility when the menu icon is clicked',function(){expect($('body').hasClass('menu-hidden')).toBeTruthy();$('.icon-list').trigger('click');expect($('body').hasClass('menu-hidden')).toBeFalsy();$('.icon-list').trigger('click');expect($('body').hasClass('menu-hidden')).toBeTruthy();});});describe('Initial Entries',function(){beforeEach(function(done){loadFeed(0,done);});it('has at least one element',function(done){expect($('.feed .entry').html()).toBeDefined();done();});});describe("New Feed Selection",function(){var firstFeed;beforeEach(function(done){loadFeed(1,function(){firstFeed=$('.feed').html();done();});});it('changes the feed content',function(done){loadFeed(0,function(){expect($('.feed').html()).not.toEqual(firstFeed);done();});});});}());